            for(int i = 0; i < n - k + 1; ++i){ 
                int j = i + k - 1; 
                if (s.charAt(i) == s.charAt(j) && table[i+1][j-1]){ 
                    table[i][j] = true; 

                    if (k > maxLength){ 
                        start = i; 
                        maxLength = k; 
                    }              
                }             
            }         
        }        

        return s.substring(start, start + maxLength); 
    }
}
